import React, { createContext, useContext } from 'react';
import axios from 'axios';

const DatabaseContext = createContext({
    fetchLanguages: () => {}, // Заглушка для предотвращения ошибки при деструктуризации
    fetchThemes: () => {},
    fetchFlashcards: () => {},
});


export const useDatabase = () => {
    return useContext(DatabaseContext);
};

export const DatabaseProvider = ({ children }) => {
    const fetchLanguages = async () => {
        try {
            const response = await axios.get('http://localhost:3002/languages');
            return response.data;
        } catch (error) {
            console.error('Error fetching languages:', error);
            return [];
        }
    };

    const fetchThemes = async (selectedLanguage) => {
        try {
            const response = await axios.get(`http://localhost:3002/themes/${selectedLanguage}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching themes:', error);
            return [];
        }
    };

    const fetchFlashcards = async (selectedLanguage, selectedTheme) => {
        try {
            const response = await axios.get(`http://localhost:3002/words/${selectedLanguage}/${selectedTheme}`);
            return response.data;
        } catch (error) {
            console.error('Error fetching flashcards:', error);
            return [];
        }
    };

    return (
        <DatabaseContext.Provider value={{ fetchLanguages, fetchThemes, fetchFlashcards }}>
            {children}
        </DatabaseContext.Provider>
    );
};
