// store.js
import { createStore } from 'redux';
import rootReducer from './reducers';

const isNode = typeof process !== 'undefined' && process.release && process.release.name === 'node'; // Check if running in Node.js environment

const store = isNode ? createStore(rootReducer) : undefined;

export default store;
